class Solution {
public:
    int findDominant(vector<int> &ones, int i){
        int j = 1;
        int ans = 0;
        int k = i;
        while(true){
            auto it = lower_bound(ones.begin(), ones.end(), j+ones[i]);
            if(it == ones.end()){
                int xx = ones.size()-k-1;
                ans += max(0, xx);
                //cout << j << ' ' << k << 'y' << ans << '\n';
                break;
            }
            int l = it - ones.begin();

            ans += max(0, l-k-1);
            //cout << j << 'x' << l << ' ' << ans << '\n';


            k = max(l, i+j+j*j);
            if(k < ones.size()){
                int xxx = ones[k] - ones[i];
                if(xxx + xxx*xxx <= k - i)
                    ans++;
            } 
            //cout << j << "xx" << k << ' ' << ans << '\n';

            if(k >= ones.size())
                break;
            j++;
        }
        //cout << i << ' ' << ans << '\n';
        return ans;
    }
    int numberOfSubstrings(string s) {
        vector<int> ones(1, 0);
        for(char c: s){
            if(c == '0')
                ones.push_back(ones.back()+1);
            else
                ones.push_back(ones.back());
        }
        int ans = 0;
        //cout << ones.size() << '\n';
        for(int i = 1; i < ones.size(); i++){
            ans += findDominant(ones, i-1);
        }
        return ans;
    }
};
