class Solution {
public:
  int numberOfSubstrings(string s) {
    vector<int> zeros = {-1};
    int n = s.size();
    int res = 0;
    for (int i = 0; i < n; ++i) {
      if (s[i] == '0') {
        zeros.push_back(i);
      }
      int prev = i;
      for (int j = (int)zeros.size() - 1; j >= 0; --j) {
        // all where r = i + 1 and l > zeros[j], l <= zeros[j + 1]
        // l <= minind
        int cz = zeros.size() - j - 1;
        int minones = cz * cz;
        int minind = i + 1 - cz - minones;
        if (minind < 0) break;
        res += max(0, min(prev, minind) - zeros[j]);
//        cerr << i << ' ' << zeros[j] << ' ' << cz << ' ' << minind << ' ' << prev << ' '
//             << res << '\n';
        prev = zeros[j];
      }
//      cerr << i << ' ' << res << '\n';
    }
    return res;
  }
};
