class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.length();
        vector<int> ones(n + 1), zeros(n + 1), nearestzero(n + 1);
        for(int i = 1; i <= s.length(); i++){
            ones[i] = ones[i-1];
            zeros[i] = zeros[i-1];
            nearestzero[i] = nearestzero[i-1];
            if(s[i-1] == '1'){
                ones[i]++;
            }else{
                zeros[i]++;
                nearestzero[i] = i;
            }
        }
        int ans = 0;
        for(int i = 1; i <= n; i++){
            for(int j = i; j >= 1;){
                int o = ones[i] - ones[j - 1], z = zeros[i] - zeros[j - 1];
                if(z > 205){
                    break;
                }
                if(o >= z * z){
                    // stays true until next zero
                    ans += j - nearestzero[j - 1];
                    j = nearestzero[j - 1];
                }else{
                    j = i - (z * (z + 1)) + 1;
                }
            }
        }
        return ans;
    }
};
