public class Solution {
    public int NumberOfSubstrings(string s) {
        var n = s.Length;

        var f = new int[n+1];
        for(var i=n-1; i>=0; i--) {
            f[i]=f[i+1];
            if (s[i]=='0') f[i]++;
        }

        var res = 0;
        for(var i =0; i <n; i++) {
            int zz = 1+(int)Math.Sqrt((n-i));

            int o=0, z=0;
            for(var j=i; j<n; j++) {
                var z2 = z+f[j];
                if (o >= z2*z2) {
                    res += (n-j);
                    break;
                }

                if (s[j]=='1') o+=1;
                else z+=1;

                if (z > zz) break;                
                if (o>=z*z) res++;
            }
        }
        return res;
    }
}
