int numberOfSubstrings(char* s) {
    int n = strlen(s);
    int *prefOnes = (int *)malloc((n + 1) * sizeof(int));
    prefOnes[0] = 0;

    for (int i = 0; i < n; ++i) {
        prefOnes[i + 1] = prefOnes[i] + (s[i] == '1' ? 1 : 0);
    }

    int ans = 0;

    for (int i = 0; i < n; ++i) {
        int r = i - 1;
        while (r + 1 < n) {
            r += 1;
            int ones = prefOnes[r + 1] - prefOnes[i];
            int zeroes = (r + 1 - i) - ones;
            if (ones >= zeroes * zeroes) {
                ans += 1;
                int skip = (int)sqrt(ones) - zeroes;
                ans += (skip < (n - r - 1)) ? skip : (n - r - 1);
                r += skip;
            } else {
                int skip = zeroes * zeroes - ones - 1;
                r += skip;
            }
        }
    }

    free(prefOnes);
    return ans;
}
