const int N=200;
const int M=4e4;
int p[M+1][N+1];
class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.size();
        int ans=0;
        for (int cnt=0;cnt<=N;cnt++){
            int i=0,j=0;
            queue <int> q;
            int f=0;
            while (j<n){
                if (s[j]=='0'){
                    q.push(j);
                    f++;
                }
                while (i<=j && f>cnt){
                    if (s[i]=='0'){
                        q.pop();
                        f--;
                    }
                    i++;
                }
                if (f==cnt){
                    int len=j-i+1;
                    if (len-cnt>=cnt*cnt && len){
                        int st=j;
                        if (!q.empty()) st=q.front();
                        int mn=min(st,j-(cnt+cnt*cnt)+1);
                      //  cout<<cnt<<" "<<i<<" "<<j<<" "<<mn<<"\n";
                        ans+=(mn-i)+1;
                      //  cout<<mn-i+1<<"\n";
                    }
                }
                j++;
            }
        }
        return ans;
    }
};
