class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<long long> z;
        int n=s.size();
        long long ans=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='0')z.push_back(i);
        }
        int pt=0;
        for(int i=0;i<n;i++)
        {
            while(pt<z.size() && z[pt]<i)pt++;
            long long ind=n;
            if(pt<z.size())ind=z[pt];
            ans+=ind-i;
            int cnt=0;
            for(int j=pt;j<z.size();j++)
            {
                cnt++;
                if(cnt>sqrt(n)+5)break;
                long long tl=z[j]-i+1;
                long long zr=j-pt+1;
                long long in=n;
                if(j+1<z.size())in=z[j+1];
                long long nx=in-z[j]-1;
                long long nd=zr*zr-(tl-zr);
                long long mn=max(z[j],z[j]+nd);
                if(nd<=nx)ans+=in-mn;
            }
            // cout<<ans<<endl;
        }
        return ans;
    }
};
