class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> z;
        int ind = 0;
        for(char c : s) {
            if(c == '0') z.push_back(ind);
            ++ind;
        }
        int n = s.size();
        int ans = 0;
        for(int i = 0; i < n; ++i) {
            auto lb = lower_bound(z.begin(), z.end(), i);
            int prev = i;
            int amt = 0;
            while(lb != z.end()) {
                ind = *lb;
                int cur_ones = ind - i - amt;
                if(cur_ones >= amt * amt) {
                    int diff = cur_ones - amt * amt;
                    ans += min(diff + 1, ind - prev);
                }
                prev = ind;
                ++amt;
                ++lb;
                if(amt * amt > n) break;
            }
            if(lb == z.end()) {
                int cur_ones = n - i - amt;
                if(cur_ones >= amt * amt) {
                    int diff = cur_ones - amt * amt;
                    ans += min(diff + 1, n - prev);
                }
            }
        }
        return ans;
    }
};
