class Solution {
public:
    int numberOfSubstrings(string s) {
        const int n = s.size();
        vector<int> one_cnts(n + 1);
        deque<int> zeros;
        zeros.push_back(n);
        int result = 0;
        for (int i = n - 1; i >= 0; --i) {
            one_cnts[i] = one_cnts[i + 1];
            if (s[i] == '1') {
                ++one_cnts[i];
            } else {
                zeros.push_front(i);
            }
            int last = i;
            int zero_cnt = 0;
            for (int zero : zeros) {
                int min_index = max(last, i + zero_cnt + zero_cnt * zero_cnt - 1);
                if (min_index < zero) {
                    result += zero - min_index;
                }
                last = zero;
                ++zero_cnt;
                if (zero_cnt * zero_cnt > n - i - zero_cnt) {
                    break;
                }
            }
        }
        return result;
    }
};
