class Solution {
public:
    int numberOfSubstrings(string s) {
      vector<int> ids[2];
      int ans = 0;
      for (int i = 0; i < size(s); i++)
      {
        ids[s[i] - '0'].push_back(i);
        if (empty(ids[0]))
        {
          ans += i + 1;
          continue;
        }
        ans += i - ids[0].back();
        for (int j = 1; j <= size(ids[0]); j++)
        {
          int cur0 = ids[0][size(ids[0]) - j];
          int next0 = -1;
          if (j + 1 <= size(ids[0]))
            next0 = ids[0][size(ids[0]) - j - 1];

          if (j * j > size(ids[1]))
            break;
          int cur1 = ids[1][size(ids[1]) - j * j];
          if (cur1 < next0)
            continue;

          ans += min(cur0, cur1) - next0;
        }
      }
      return ans;
    }
};
