class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.length();
        int pre[n];
        pre[0]=s[0]-'0';
        for(int i=1;i<n;i++)
        {
            pre[i]=pre[i-1]+(s[i]-'0');
        }
        int ans=0;
        for(int l=0;l<n;l++)
        {
            int r=l;
            while(r<n)
            {
                int one=pre[r]-(l>0?pre[l-1]:0);
                int zero=(r-l+1-one);
                if(zero*zero<=one)
                {
                    ans++;
                    int dif=min((int)sqrt(one)-zero,n-r-1);
                    ans+=dif;
                    r+=dif+1;
                }
                else 
                {
                    int dif=max(zero*zero-one-1,0);
                    r+=dif+1;
                }
            }
        }
        return ans;

    }
};
