class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> idx;
        idx.push_back(-1);
        int cnt=0, ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0') idx.push_back(i);
            else cnt++;
            ans+=(i-idx[idx.size()-1]);
            for(int j=idx.size()-1;j>0;j--){
                int x=idx.size()-j;
                int y=x*x;
                int r=min(idx[j],i-x-y+1);
                if(r<0) break;
                int l=idx[j-1]+1;
                if(r-l+1 < 0) continue;
                ans+=r-l+1;
            }
        }
        return ans;
    }
};
