class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.size();
        vector<int>pre(n,0);
           int tz=sqrt(n);
       vector<int>si[n+1];
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='0')
                pre[i]=1;
            else
                pre[i]=0;
            if(i)
            pre[i]+=pre[i-1];
            si[pre[i]].push_back(i);
        }
        int ans=0;
        // for(int i:si[3])
        // {
        //     cout<<i<<" ";
        // }
        // cout<<endl;

        for(int i=0;i<n;i++)
        {
            int last =0;
            if(i)
                last=pre[i-1];
            for(int j1=last;j1<=last+tz;j1++)
            {
                int j=j1-last;
                if((j*j+j+i-1<=n-1)&&(si[j1].size()))
                {
                int in=((int)si[j1].size()-(lower_bound(si[j1].begin(),si[j1].end(),max(i,j*j+j+i-1))-si[j1].begin()));
                ans+=in;
                   // cout<<i<<" "<<in<<" "<<j<<" "<<j1<<endl;
                }

            }

        }
        return ans;

    }
};
