class Solution {
public:
    int f(vector<int>& zeros, vector<int>& ones) {
        if(zeros.empty()) {
            return ones.size(); 
        }
        if(ones.empty()) {
            return 0; 
        }
        int ret = max(0, ones.back() - zeros.back()); 
        //cout<<"here "<<"ret = "<<ret<<endl;
        for(int i = 1;i<zeros.size();i++) {
            long long one = static_cast<long long>(i) * i; 
            if(one > ones.size()) {
                break; 
            }
            int r = ones[ones.size() - one]; 
            if (r > zeros[zeros.size() - i - 1]) {
                r = min(zeros[zeros.size() - i], r); 
                ret += r - zeros[zeros.size() - i - 1];
            } 
            //cout<<"i = "<<i<<" ret = "<<ret<<endl;
        }
        long long one = static_cast<long long>(zeros.size()) * zeros.size(); 
        if(one <= ones.size()) {
            int r = ones[ones.size() - one]; 
            r = min(zeros.front(), r); 
            ret += r + 1; 
        }
        return ret; 
    }

    int numberOfSubstrings(string s) {
        vector<int> ones;// = {3};
        vector<int> zeros;// =  {0,1,2};
        int ret = 0; 

        //cout<<"test = "<<f(zeros, ones)<<endl;
        for(int i = 0;i<s.size();i++) {
            if(s[i] == '1') {
                ones.push_back(i);
            } else {
                zeros.push_back(i); 
            }

            ret += f(zeros, ones); 
            //cout<<" i = "<<i<<" ret = "<<ret<<endl;
        }
        return ret; 
    }
};
