class Solution {
public:
    int find(int z, int o){
        if(z*z>=o) return 0 ;
        int start = 0 ;
        int end = 300 ;
        int ans = 0 ;
        while(start<=end){
            int m = (start+end)/2 ;
            int a = z+m ;
            if(a*a<=o){
                ans = m;
                start = m+1 ;
            }   
            else{
                end = m-1 ;
            }
        }
        return ans ;
    }
    int numberOfSubstrings(string s) {

        int n = s.size() ;       
        vector<int> dp(s.size()) ;
        dp[s.size()-1] = 1 ;
        for(int i=s.size()-2;i>=0;i--){
            if(s[i]==s[i+1]){
                dp[i] = 1+dp[i+1] ;
            }
            else{
                dp[i] = 1 ;
            }
        }

        int ans = 0;

        for(int i=0;i<n;i++){
            int z = 0 ;
            int o = 0 ;
            if(s[i]=='1'){
                ans++ ;
                o++ ;
            }
            else{
                z++ ;
            }
            for(int j=i+1;j<n;){
                if(s[j]=='0'){
                    int count = dp[j] ;
                    int p = find(z,o) ;
                    ans = ans + min(p, count) ;
                    z = z + dp[j] ;
                    j = j + dp[j] ;
                }
                else{
                    int r = o-z*z ;
                    int count = dp[j] ;
                    int g = min(0, r) ;
                    if(g<0){
                        g++ ;
                    }
                    count+=g ;
                    if(count<0) count = 0;
                    ans+=count ;
                    o = o + dp[j] ;
                    j = j + dp[j]  ;
                }
                if(z>400){
                    break ;
                }
            }
            //cout<<i<<" "<<ans<<endl ;
        }

        return ans ;
    }
};
