class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.size();
        unordered_map<int,int> zeros;
        unordered_map<int,int> ones;

        zeros[0]=-1;
        ones[0]=-1;

        int z=0;
        int o=0;

        for(int i=0;i<s.size();i++)
        {
            char x=s[i];
            if(x=='0')
            {
                z++;
                zeros[z]=i;
            }
            else
            {
                o++;
                ones[o]=i;
            }
        }

        z=0;
        o=0;

        //for each starting point, if z and o are seen until now
        //fing position of z+k and z+k+1 in zeros and o+k^2 in ones
        int ans=0;
        for(int i=0;i<s.size();i++)
        {
            //cout<<"i "<<i<<endl;
            //cout<<z<<" "<<o<<endl;
            char x=s[i];
            int k=0;
            //if(x=='0')  k=1;
            for(;k<=200;k++)
            {
                int z_k_pos=n;
                int z_k_1_pos=n;
                int o_k_2_pos=n;

                if(zeros.find(z+k)==zeros.end())    break;
                if(ones.find(o+k*k)==ones.end())    break;

                z_k_pos=zeros[z+k];
                o_k_2_pos=ones[o+k*k];
                if(zeros.find(z+k+1)!=zeros.end())  z_k_1_pos=zeros[z+k+1];

                if(o_k_2_pos>z_k_1_pos) continue;

                //o_k_2_pos=max(o_k_2_pos,i);

                if(o_k_2_pos<z_k_pos)
                {
                    ans+=z_k_1_pos-z_k_pos;
                }
                else
                {
                    ans+=z_k_1_pos-o_k_2_pos;
                }
            }

            //cout<<ans<<endl;


            if(x=='0')
            {
                z++;
            }
            else
            {
                o++;
            }
        }

        return ans-n;
    }
};
