class Solution {
public:
    int numberOfSubstrings(string s) {
          int n = s.size();
          vector<int> next(n, n);
          vector<int> pr(n , 0);
        for(int i = 0 ; i < n ;i++){
            pr[i] = s[i] == '1';
            if(i)pr[i] += pr[i-1];
        }
        auto get = [&](int l  , int r){
            return pr[r] - (l?pr[l-1] : 0); 
        };
           int z = n;
        for(int i = n-1;i>=0;i--){
            next[i] = z; 
            if(s[i] == '0')z = i;
        }
        using ll = long long;
        int p = -1;
        ll ans = 0;
        for(int i = 0 ; i < n ;i++){


             for(int j = i ; j < n ; j = next[j]){
                 ll one = get(i , j) , zero = j - i + 1 - one;
                 if(zero * zero >= n)break;
                 if(zero * zero <= one){
                     ans += next[j] - j;
                 }else{
                     ll req = zero * zero - one;
                     if(next[j] - j - 1 >= req){
                         ll l = j + req;
                         ans += next[j] - l;
                     }
                 }
             }
        }

        return ans;

    }
};
