class Solution {
public:
    int numberOfSubstrings(string s) {
        set<int> s1;
        vector<int> pre;
        int temp=0;
        for(int j=0;j<s.size();j++){
            if(s[j]=='0'){
                temp+=1;
                s1.insert(j);
            }
            pre.push_back(temp);
        }
        int ans=s.size()*(s.size()+1)/2;

        for(int j=0;j<s.size();j++){

            int i=j;

            int x=-1;
            while(i<s.size()){
                int temp=pre[i];
                if(j){
                    temp-=pre[j-1];
                }
                int temp1=(i-j+1)-temp;

                if(temp*temp<=temp1){
                    if(x!=-1){
                        ans-=(i-x);
                        x=-1;
                    }
                    auto it=s1.upper_bound(i);
                    if(it==s1.end()){
                        i=s.size();
                    }else{
                        i=*it;
                    }
                }else{
                    if(x==-1){
                        x=i;
                    }
                    i=j+temp*temp+temp-1;
                }
            }
            if(x!=-1){
                cout << x << " ";
                ans-=(s.size()-x);
            }
            cout << ans << " ";
        }

        return ans;
    }
};
