class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        prefs0 = [0 for i in range(n)]
        prefs1 = [0 for i in range(n)]

        if s[0] == '0':
            prefs0[0] = 1
        else:
            prefs1[0] = 1

        for i in range(1,n):
            prefs0[i] = prefs0[i-1]
            prefs1[i] = prefs1[i-1]
            if s[i] == '0':
                prefs0[i] += 1
            else:
                prefs1[i] += 1

        next_zero = [n for i in range(n)]
        for i in range(n-2, -1, -1):
            if s[i+1] == '0':
                next_zero[i] = i+1
            else:
                next_zero[i] = next_zero[i+1]

        ans = 0
        # prefs 
        for j in range(n):
            zero_ct = prefs0[j]
            ones_ct = prefs1[j]
            if ones_ct >= zero_ct*zero_ct:
                ans += 1

        for i in range(1,n):
            j = i
            while j < n:
                zero_ct = prefs0[j] - prefs0[i-1]
                ones_ct = prefs1[j] - prefs1[i-1]
                zero_ct_sq = zero_ct*zero_ct
                if ones_ct >= zero_ct_sq:
                    ans += next_zero[j] - j
                    j = next_zero[j]
                else:
                    j += zero_ct_sq - ones_ct


        return ans
