class Solution {
public:
    int numberOfSubstrings(string s) {
        int ans = 0;
        int n = s.size();
        int nxt[n+1];
        nxt[n] = n;
        for(int i =n-1;i>=0;i--){
            if(s[i]=='0'){
                nxt[i] = i;
            }
            else{
                nxt[i] = nxt[i+1];
            }
        }
        for(int i = 0 ;i<n;i++){
            int lst = i;
            int cur = nxt[i];
            int itr = 0;
            while(1){
                int ones = cur-i-itr;
                int diff = ones-itr*itr+1;
                if(diff<-n*2)break;
                int cur_ans = min(diff,cur-lst);

                ans+=max(0,cur_ans);
                // cout<<i<<' '<<cur<<' '<<cur_ans<<' '<<diff<<endl;

                lst = cur;
                itr++;
                if(cur==n)break;
                else{
                    cur = nxt[cur+1];
                }
            }
        }
        return ans;
    }
};
