class Solution {
public:
    typedef long long ll;
    int numberOfSubstrings(string s) {
        int n = s.size();
        ll a = 0;
        ll b = 0;
        ll res = 0;

        unordered_map<int, vector<int>> data;

        data[0].push_back(0);

        for (int i = 0; i < n; i++) {
            int x = s[i] - '0';

            if (x) {
                a += 1;
            } else {
                b += 1;
            }

            for (ll c = 0; c <= b && c * c <= a; c++) {
                ll tb = b - c; // c = b - tb
                ll ta = a - (c * c); // c * c <= a - ta 

                auto f = lower_bound(data[tb].begin(), data[tb].end(), ta+1);

                int sub = f - data[tb].begin();

                res += sub;
            }


            data[b].push_back(a);
        }

        return res;
    }
};
