class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        prev = [-1 for i in range(n + 1)]
        result = 1 if s[0] == '1' else 0
        prev[0] = -1 if s[0] == '1' else 0
        for k in range(1, n):
            i = k
            t = 1 if s[k] == '0' else 0
            prev[k] = k if s[k] == '0' else prev[k - 1]
            while i >= 0:
                j = prev[i - 1]
                # print(k, ' ', i, ' ', j, ' ', t)
                if t * t < k - i + 1 - t:
                    result = result + (i - j)
                elif t * t <= k - j - t:
                    result = result + (k - j - t) - t * t + 1
                i = j
                t += 1
                if (t * t > k):
                    break
        return result
