class Solution:
    def numberOfSubstrings(self, s: str) -> int:

        def atmost(s, zeroCount, minLen):
            if minLen > len(s):
                return 0
            n = len(s)
            left = 0
            c = 0
            ans = 0
            for i in range(n):
                if s[i] == '0':
                    c += 1
                while left <= i and c > zeroCount:
                    if s[left] == '0':
                        c -= 1
                    left += 1
                if i - left + 1 >= minLen:
                    ans += i - left + 1 - minLen + 1
            return ans

        z = 1
        oc = s.count('1')
        ret = atmost(s, 0, 1)
        while z * z <= oc:
            l = z + z * z
            ret += atmost(s, z, l) - atmost(s, z-1, l)
            z += 1
        return ret
