class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> zeros;
        for(int i=0;i<s.length();i++){
            if(s[i]=='0')
                zeros.push_back(i);
        }
        int answer=0;
        for(int i=0;i<s.length();i++){
            int x=i;
            int zz=203;
            if(zeros.size()==0){
                answer+=(s.length()-i);
            }
            else if(zeros[zeros.size()-1]<i){
                answer+=(s.length()-i);
            }
            else{
                int index=lower_bound(zeros.begin(),zeros.end(),x)-zeros.begin();
                int bb=index;
                int z=0;
                int o=0;
                int lastindex=i;
                while(index<zeros.size()&&zz>0){
                    //cout<<zeros[index]<<" "<<lastindex<<"****\n";
                    if(lastindex<zeros[index])
                    {
                        answer+=(zeros[index]-lastindex);                    
                    }
                    z++;

                    lastindex=max(z*z+z+i-1,zeros[index]);
                    index++;
                    zz--;
                    //cout<<lastindex<<" "<<answer<<"##**\n";
                }
                if(lastindex<=s.length()){
                    answer+=(s.length()-lastindex);
                }
            }
            //cout<<answer<<"-------------------- "<<i<<endl;
        }

        return answer;
    }
};
