class Solution {
    public int numberOfSubstrings(String s) {
        int n = s.length();

        int result = 0;

        int left[] = new int[n+1], right[] = new int[n+2];

        List<Integer> zeroIndex = new ArrayList<>();

        for(int i=1; i<=n; i++)
        {
            if(s.charAt(i-1)=='1')
            {
                left[i] = 1 + left[i-1];
                result += left[i];
            } else {
                zeroIndex.add(i);
            }
        }

        for(int i=n; i>=1; i--)
        {
            if(s.charAt(i-1)=='1')
            {
                right[i]=1 + right[i+1];
            }
        }

        // System.out.println("here " + result);

        for(int i=1; i<=200; i++)
        {
            for(int j=0; j<zeroIndex.size()-i+1; j++)
            {
                int l = zeroIndex.get(j) , r = zeroIndex.get(j+i-1);

                int lval = left[l-1] , rval = right[r+1];

                int req = Math.max(i*i + i -(r-l+1),0);

                // System.out.println(i+" "+l+" "+r+" "+req+" "+lval+" "+rval);
                int cnt = 0;

                for(int x=0; x<=lval; x++)
                {
                    if(rval>=req-x)
                    {
                        cnt += (rval-Math.max(0, req-x)+1);
                    }
                }

//                 for(int x=0; x<=rval; x++)
//                 {
//                     if(lval>=req-x)
//                     {
//                         result += (lval-Math.max(1, req-x)+1);
//                         cnt += (lval-Math.max(1, req-x)+1);

//                     }
//                 }

                result += cnt;
            }
        }


        return result;


    }
}
