class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.length();
        vector<int>zero,one;
        for(int i=0;i<n;i++){
            if(s[i]=='0')zero.push_back(i);
            else one.push_back(i);
        }
        int ans=0;
        zero.push_back(n);
        int sz=zero.size();
        for(int z=1;z*z<=n;z++){
            if(z>sz-1)continue;
            for(int i=0;i<n;i++){
                int it1=lower_bound(zero.begin(),zero.end(),i)-zero.begin();
                if(it1+z>=sz)break;
                int ind1=zero[it1+z-1];
                int ind2=zero[it1+z];
                int it2=lower_bound(one.begin(),one.end(),i)-one.begin();
                if(it2+z*z-1>=one.size())break;
                int ind3=one[it2+z*z-1];
                if(ind2-max(ind1,ind3)>0)ans+=(ind2-max(ind1,ind3));
            }
        }
        for(int i=0;i<n;i++){
            if(s[i]=='0')continue;
            int j=i;
            while(j<n){
                if(s[j]=='1')j++;
                else break;
            }
            int cnt=j-i;
            ans+=(cnt*(cnt+1))/2;
            i=j-1;
        }
        return ans;
    }
};
