class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> zeros;
        int ans = 0;
        for (int i = 0; i < s.size(); ++i) {
            if (s[i] == '0') {
                zeros.push_back(i);
            }

            for (int j = 0; j <= 200 && j <= zeros.size(); ++j) {
                int right = i;
                if (j > 0) right = zeros[zeros.size() - j];
                int left = 0;
                if (j + 1 <= zeros.size()) left = zeros[zeros.size() - j - 1] + 1;

                int left0 = left;

                if (left > right) continue;
                int ones = i - left + 1 - j;
                if (ones < j * j) continue;

                while (left < right) {
                    int mid = ((left + right) >> 1) + 1;
                    int ones = i - mid + 1 - j;
                    if (ones < j * j) {
                        right = mid - 1;
                    } else {
                        left = mid;
                    }
                }
                ans += right - left0 + 1;
            }
        }
        return ans;
    }
};
