class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        r = 0
        for num_zeros in range(min(s.count('0')+1, int(len(s)**.5)+1)):
            window = deque()
            z = ones = 0
            start_idx = 0
            for i, c in enumerate(s):
                z += c == '0'
                ones += c == '1'
                window.append(i)
                while z > num_zeros:
                    idx = window.popleft()
                    z -= s[idx] == '0'
                    ones -= s[idx] == '1'
                    start_idx = idx + 1
                if z == num_zeros and ones>=z*z:
                    while window and s[window[0]] == '1' and (ones-1)>=z*z:
                        #print(ones)
                        window.popleft()
                        ones -= 1
                        #print('popped',ones)
                    if window:
                        #print(start_idx, window[0], window)
                        r += window[0] - start_idx + 1
                    else:
                        r += i - start_idx + 1
        return r
