class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
        long long ans=0;
        vector<int>v(n+10,0);
        int c=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='1')c+=1;
            else c=0;
            v[i]=c;
            ans+=c;
        }

        for(int ct=1;ct<=sqrt(n)+1;ct++){

            int one=0;int zero=0;int j=0;

            for(int i=0;i<n;i++){
                if(s[i]=='0')zero+=1;

                if(zero==ct){

                    int k=j;

                    while(s[j]!='0')j++;
                    while(s[k]=='0' && k<j)k++;

                    one = i-j+1-ct;

                    while(k<=j){

                        int x = j-k+one;

                        if(x+v[i+1]>= ct*ct){

                            int y  = ct*ct - x;
                            if(y<0)y=0;
                            ans += v[i+1]-y+1;

                        }
                        k++;
                    }
                    zero--;j++;
                }

            }

        }
        return ans;
    }
};
