import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

class Solution {
    public int numberOfSubstrings(String s) {
        // for each i
        // find prev 0; location
        List<Integer> pos = new ArrayList<>();
        pos.add(-1);
        int ans = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '0')
                pos.add(i);
            tr(">>>",i, pos);
            int r = i;
            for (int j = 0; j < pos.size(); j++) {
                int left = pos.get(pos.size() - 1 - j);
                // j*j <= i-x+1-j
                // x<=i+1-j*j-j;
                int x = i + 1 - j * j - j;
                tr(i, j, j * j, x);
                x = Math.min(r, x);
                if (x<0)
                    break;
                ans += Math.max(0, x - left);
                r = left;
                tr(">", i, j, ans);
            }
        }
        return ans;
    }

    static void tr(Object... o) {
        // System.out.println(Arrays.deepToString(o));
    }

}
