class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
        long long ans = 0;
        vector<int> prefix(n+1,0);
        for(int i = 1; i <= n; i++)
            prefix[i]=prefix[i-1]+(s[i-1]=='1' ? 1:0);

        for(int i = 0; i < n; i++){
            int r = i;
            while(r < n){
                int num = prefix[r+1]-prefix[i];
                int sz = r+1-i;
                if(num >= (sz-num)*(sz-num))
                {
                    if ((int)sqrt(num) > (sz-num))
                    {
                        ans += min(n-r,(int)sqrt(num)-sz+num);
                        r += min(n-r,(int)sqrt(num)-sz+num);
                    }
                    else
                    {
                        ans++;
                        r++;
                    }
                }

                else
                    r+=(sz-num)*(sz-num)-num;
            }
        }
        return ans;
    }
};
