class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.size();
        int z=0;
        for(int i=0;i<n;i++)
            if(s[i]=='0')
                z++;
        int ans=0;
        for(int nz=0;nz<=sqrt(n);nz++){
            int j1=0;
            int cnt=0;
            while(j1<n&&cnt<nz){
                if(s[j1]=='0')
                    cnt++;
                j1++;
            }
            if(cnt<nz)
                break;
            int j2=j1;
            while(j2<n&&s[j2]!='0'){
                j2++;
            }
            for(int i=0;i<n;i++){
                int omin=j1-i-nz,omax=j2-i-nz;
                int thres=max(nz*nz,1);
                if(thres<=omax)
                    ans+=omax-max(omin,thres)+1;
                if(s[i]=='0'){
                    if(j2==n)
                        break;
                    j1=j2+1;
                    j2=j1;
                    while(j2<n&&s[j2]!='0'){
                        j2++;
                    }
                }
            }
        }
        return ans;
    }
};
