lookup = [0] * 201
for i in range(1, 201):
    lookup[i] = i*(i+1)

class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        seen_zero_idx = []
        res = 0
        for i in range(n):
            if s[i] == "0":
                seen_zero_idx.append(i)
            else:
                next = seen_zero_idx[-1] if seen_zero_idx else -1
                res += i-next

            m = len(seen_zero_idx)

            for j in range(m-1, max(-1, m-201), -1):
                cur = seen_zero_idx[j]
                next = seen_zero_idx[j-1] if j else -1
                cur_zero_cnt = m-j
                target = lookup[cur_zero_cnt]
                target_index = min(cur, i-target+1)
                res += max(0, target_index-next)

        return res
