class Solution {
public:
    int numberOfSubstrings(string s) {
        // 111 00 11 
        // check no 0 zeros, 1 zeros, 2 zeros... up to k zeros

        int ans = 0;
        int n = s.size();
        vector<int> zeroIdx;
        for( int i = 0; i < n; ++i ) {
            if( s[ i ] == '0' ) {
                int zeros = 1;
                int ones = 0;
                zeroIdx.push_back( i );

                for( int j = zeroIdx.size() - 1; j >= 0; --j ) {
                    int gain = j == 0 ? zeroIdx[ j ] : zeroIdx[ j ] - zeroIdx[ j - 1 ] - 1;
                    int need = zeros * zeros - ones;

                    if( need <= 0 ) {
                        ans += gain + 1;
                    }
                    else if( gain >= need ) {
                        ans += gain - need + 1;
                    }

                    ones += gain;
                    ++zeros;

                    if( zeros * zeros > i ) {
                        break;
                    }
                }
                //cout<<i<<","<<ans<<"\n";
            }
            else {
                int ones = zeroIdx.empty() ? i + 1 : i - zeroIdx.back();
                int zeros = 1;
                ans += ones;

                for( int j = zeroIdx.size() - 1; j >= 0; --j ) {
                    int gain = j == 0 ? zeroIdx[ j ] : zeroIdx[ j ] - zeroIdx[ j - 1 ] - 1;
                    int need = zeros * zeros - ones;

                    if( need <= 0 ) {
                        ans += gain + 1;
                    }
                    else if( gain >= need ) {
                        ans += gain - need + 1;
                    }

                    ones += gain;
                    ++zeros;

                    if( zeros * zeros > i ) {
                        break;
                    }
                }
                //cout<<i<<","<<ans<<"\n";
            }
        }

        return ans;
    }
};
