class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.size(),ans=0;
        vector<int> zeros;
        zeros.push_back(-1);
        vector<int> pre(n,0);
        for(int i=0;i<n;i++){
            if(s[i]=='1') pre[i]++;
            if(i>0)pre[i]+=pre[i-1];
            int sz=zeros.size(),cnt=0;
            if(s[i]=='0') cnt++;
            for(int j=sz-1;j>=0;j--){
                if(cnt*cnt>n) break;
                // if(i==2) cout<<zeros[j]<<' '<<cnt<<endl;
                int ones=pre[i];
                if(j>0) ones-=pre[zeros[j]];
                if(ones<cnt*cnt){
                    cnt++;
                    continue;
                }
                int prev=pre[i];
                if(j<sz-1) prev=pre[zeros[j+1]];
                if(j>0) prev=prev-pre[zeros[j]];
                if(cnt>0) ans++;
                ans=ans+min(ones-cnt*cnt,prev);
                cnt++;
            }
            // cout<<i<<" "<<ans<<endl;
            if(s[i]=='0') zeros.push_back(i);
        }
        return ans;
    }
};
