class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        zeros = [-1]
        ans = 0
        for i, c in enumerate(s):
            if c == '0':
                zeros.append(i)
            else:
                ans += i - zeros[-1]
            for j in range(len(zeros)-1, 0, -1):
                z = len(zeros) - j
                if z * z > i + 1 - z:
                    break
                r = min(zeros[j], i + 1 - (z + (z * z)) )
                l = zeros[j-1] + 1
                # print(i, j, l, r, z)
                if r >= l:
                    ans += r - l + 1
            # print(i, c, ans)
        return ans
