using ll=long long;

using namespace std;

class Solution {
public:

    int numberOfSubstrings(string a){
        int n=a.size();
        a=' '+a;
        vector<int> v;
        for(int i=1; i<=n; i++){
            if(a[i]=='0'){
                v.push_back(i);
            }
        }
        v.push_back(n+1);
        int res=0;
        for(int i=1; i<=n; i++){
            //cout << "---- " << i << '\n';
            int id=lower_bound(begin(v), end(v), i)-begin(v);
            res+=max(0, v[id] - (i));
            //cout << i << ' ' << id << ' ' << v[id] << ' ' << v[id]-i << '\n';
            int s=1;
            while(id+1 < v.size() &&  s*s+s<=(n-i+1)){
                res+=max(0, v[id+1] - max(v[id], (i+s*s+s-1)));
                //cout << v[id+1] << ' ' << s << ' ' << id << ' ' << max(v[id], (i+s*s+s-1)) << ' ' << v[id+1] - max(v[id], (i+s*s+s-1)) << '\n';
                ++id;
                ++s;
            }
        }
        return res;
    }
};
