class Solution {
public:
    int numberOfSubstrings(string s) {
        int totalZeros=0;
        vector<int> zeros;
        vector<int> ones(s.size());
        for(int i=0;i<s.size();i++){
            if(s[i]=='0'){
                totalZeros++;
            }
            if(s[i]=='1'){
                if(i==0) ones[i]=1;
                else ones[i]= ones[i-1]+1;
            }
            zeros.push_back(totalZeros);
        }
        int cnt=0;
        for(int i=0;i<s.size();i++){
            int tz=0;
            for(int j=i;j>=0;j--){
                tz=zeros[i]; 
                if(j>0) tz = tz-zeros[j-1];
                if(i-j+1-tz>=tz*tz){
                    if(j>0){ 
                        cnt=cnt+ones[j-1]+1;
                        j=j-ones[j-1];
                    }
                    else cnt++;
                }
                else{
                    j=i-(tz*tz)-tz+2;
                }
            }
        }
        return cnt;
    }
};
