class Solution {
public:
    int solve(const vector<int>&pre, const vector<int>&idx,const int r){
        int ans = 0 , sz = idx.size(), z=0;
        for(int k=sz-1,j=r-1;k>=0 && k>=sz-201 && z*z<=pre[r];--k){
            int i = idx[k];
            // [i...j] , p[t] <= p[r]-z*z
            int tt = pre[r]-z*z;
            int ub = upper_bound(begin(pre), begin(pre)+r, tt)- begin(pre)-1;
            if(i<=ub) ans += min(j,ub)-i+1;
            j = i-1,++z;
        }
        return ans;
    }
    int numberOfSubstrings(string s) {
        int n = s.size();
        vector<int> p(n+1,0);
        vector<int> idx = {0};
        int ans = 0;

        for(int i=1;i<=n;++i){
            int d = s[i-1]-'0';
            if(d==0) idx.push_back(i);
            p[i] = p[i-1] + d;
            ans += solve(p, idx,i);
        }
        return ans;
    }
};
