class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
        vector<int>zero(n, 0);
        for(int i = 0; i < n; i++){
            zero[i] = (s[i] == '0');
            if(i > 0)
                zero[i] += zero[i-1];
        }
        set<int>st{n};
        for(int i = 0; i < n; i++){
            if(s[i] == '0')
                st.insert(i);
        }
        int ans = 0, c = 0;
        for(int i = 0; i < n; i++){
            if(s[i] == '1')c++;
            else c = 0;
            ans += c;
        }
        for(int i = 0; i < n; i++){
            auto iter1 = st.lower_bound(i);
            for(int j = 1; j <= n; j++){
                int min_pos = i + j + j*j - 1;
                if(((*iter1) == n) || (min_pos >= n))break;
                int p1 = *iter1, p2 = *next(iter1)-1;
                p1 = max(p1, min_pos);
                if(p1 <= p2){
                    ans += (p2 - p1 + 1);
                } 
                iter1 = next(iter1);
            }
        }
        return ans;
    }
};
