class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.size();
        unordered_map<int,vector<int>>mp;
        int o=0,z=0;
        mp[0].push_back(0);int ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1')o++;
            else z++;

            for(int zz=0;zz<=z && (zz*zz<=o);zz++){
                int req=z-zz;
                int cnt=o-(zz*zz);
                auto it=upper_bound(mp[req].begin(),mp[req].end(),cnt);
                int cc=(it-mp[req].begin());
                ans+=cc;
            }
            mp[z].push_back(o);
        }
        return ans;
    }
};
