class Solution {
public:
    int numberOfSubstrings(string s) {
        //x 0, x 1, >4 2, >9 3, >16 4
        int n = 201;
        vector<int>zer(n,-1);
        int ans = 0;
        for(int i=0;i<s.size();i++){
            if (s[i]=='1'){
                if(zer[1]==-1){
                    ans+=i+1;
                    continue;
                }
                for(int j = 1;j<n;j++){
                    if(j==1){
                        ans+=(i - zer[j]); // 1 2 3 4
                        int ones = i - zer[j]+1 - j; // 1 2 3 4
                        int x3 = ones - j*j; // 0 1 2 3
                        if(x3>=0)ans++;
                        //cout<<i<<" "<<ans<<endl;
                    }
                    else if(zer[j]!=-1){
                        int totalOnes = i - zer[j-1]+1 - (j-1);
                        int x = totalOnes - (j-1)*(j-1);
                        //cases
                        if(x>=0){
                            ans += (zer[j-1] - zer[j] - 1);
                        }
                        else{
                            //cases
                            int cur = i - zer[j] - (j-1);
                            int x2 = cur - (j-1)*(j-1);
                            if (x2>=0){
                                ans+=(x2+1);
                            }
                        }
                        x = i - zer[j]+1 - j - j*j;
                        if(x>=0)ans++;
                    }
                    else{
                        int totalOnes = i - zer[j-1]+1 - (j-1);
                        int x = totalOnes - (j-1)*(j-1);
                        //cases
                        if(x>=0){
                            ans += (zer[j-1]);
                        }else{
                            int cur = i+1 - (j-1);
                            int x2 = cur - (j-1)*(j-1);
                            if (x2>=0){
                                ans+=(x2+1);
                            }
                        }
                        //cout<<i<<" "<<ans<<endl;
                        break;
                    }
                }
            }else{
                for(int i=n-1;i>=2;i--){
                    if(zer[i-1]==-1)continue;
                    zer[i] = zer[i-1];
                }
                zer[1] = i;
                for(int j = 1;j<n;j++){
                    if(j==1){
                        ans+=(i - zer[j]);
                        int ones = i - zer[j]+1 - j;
                        int x3 = ones - j*j;
                        if(x3>=0)ans++;
                    }
                    else if(zer[j]!=-1){
                        int totalOnes = i - zer[j-1]+1 - (j-1);
                        int x = totalOnes - (j-1)*(j-1);
                        //cases
                        if(x>=0){
                            ans += (zer[j-1] - zer[j] - 1);
                        }
                        else{
                            //cases
                            int cur = i - zer[j] - (j-1);
                            int x2 = cur - (j-1)*(j-1);
                            if (x2>=0){
                                ans+=(x2+1);
                            }
                        }
                        x = i - zer[j]+1 - j - j*j;
                        if(x>=0)ans++;
                    }
                    else{
                        int totalOnes = i - zer[j-1]+1 - (j-1);
                        int x = totalOnes - (j-1)*(j-1);
                        //cases
                        if(x>=0){
                            ans += (zer[j-1]);
                        }else{
                            int cur = i+1 - (j-1);
                            int x2 = cur - (j-1)*(j-1);
                            if (x2>=0){
                                ans+=(x2+1);
                            }
                        }
                        break;
                    }
                }
            }
        }
        return ans;
    }
};
