class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
    int count = 0;
    int d=0;

    vector<int> v;
    for(int i=0;i<s.size();i++){
      if(s[i]=='0')  v.push_back(i);
    }



    // Iterate through all possible substrings
    for (int i = 0; i < n; ++i) {

        int x=lower_bound(v.begin(),v.end(),i)-v.begin();

        int z=0;
        int p=-2;
        for (int j = x; j <x+202  && j<v.size(); ++j) {
               z++;
               int ind=v[j];
             int range=z*z-1+z+i-1;

              if(range>=ind){
                range=min(range,n-1);

                int ind2=ind;
                ind2=max(ind2,p+1);
                p=range;
              if(range>=ind2)  count+=range-ind2+1;
              if(range>=n-1)break;
              }

        }
    }

    return (n*(n+1)/2)-count;
    }





};
