class Solution {
public:
    int numberOfSubstrings(string s) {
        int sz = s.size();
        vector<int> p(sz+1);
        int r = 0;
        for(int i=0;i<sz;i++) p[i+1]=p[i]+(s[i]=='1');
        int o = 0;
        int z = 0;
        int vz = 0;
        for(int i=0;i<sz;i++)
        {
            //cout<<"I: "<<i<<endl;
            int ju = 1;
            for(int j=i;j<sz;)
            {
                o=p[j+1]-p[i];
                z=(j-i+1)-o;
                vz = z*z;
                if(o>=vz) r+=ju;
                //cout<<j<<" "<<r<<endl;
                if(j==sz-1) break;
                if(vz>o)
                {
                    j+=vz-o;
                    ju=1;
                }
                else
                {
                    int nz = sqrt(o);
                    int d = max(1,nz-z);
                    if(d+j>=sz) d=sz-1-j;
                    ju=d;
                    j+=d;
                }
            }
        }
        return r;
    }
};
