class Solution {
public:
    int numberOfSubstrings(string s) {

        int ans=0;

         vector<int> zero;

         int n=s.length();
         int len=0;
         for(int i=0;i<n;i++){
             if(s[i]=='0'){
                 zero.push_back(i);
                 ans+=len*(len+1)/2;
                 len=0;
             }
             else{
                 len+=1;   
             }
         }

         ans+=len*(len+1)/2;

         zero.push_back(n);

        for(int i=0;i<n;i++){
             int idx=lower_bound(zero.begin(),zero.end(),i)-zero.begin();
            for(int j=idx;j<zero.size()-1;j++){
                int val=j-idx+1; 
                int one=zero[j]-i+1-val;
                int add=zero[j];
                if(one<val*val){
                    add+=val*val-one;
                }
                if(add>n)break;
                if(add>=zero[j+1])continue;



                    // cout<<val<<endl;
                    // cout<<i<<" "<<zero[j]<<" "<<add<<" "<<zero[j+1]<<endl;

                ans+=max(0,zero[j+1]-add);


            }
        }

        return ans;
    }
};
