class Solution {
public:
    int numberOfSubstrings(string s) {
        int n=s.length();
        vector<int> nxt(n);      
        nxt[n-1]=n;
        for(int i=n-2;i>=0;i--){
            if(s[i+1]=='0')nxt[i]=i+1;
            else nxt[i]=nxt[i+1];
        }
        int ans=0;
        for(int i=0;i<n;i++){
            int zero=0;
            int j=i;
            if(s[i]=='0') zero++;
            while(j<n && zero<200){

                int till=j;
                int ones=(j-i+1)-zero;
                int need=(zero*zero)-ones;
                 // cout<<i<<" "<<j<<" "<<ones<<endl;
                j=nxt[j];
                if(need<=0) ans+=(j-till);
                else{
                    int len=j-till;
                    ans+=max(0,len-need);
                }
                zero++;



            }
        }
        // cout<<ans<<endl;
        return ans;

    }
};
