vector<int> v[40005];
class Solution {
public:
    int numberOfSubstrings(string s) {
        for(int i=0;i<=s.size();i++)v[i].clear();
        v[0].push_back(0);

        int ans[2]={0,0};
        int res=0;
        for(auto it:s){
            ans[it-'0']++;
            res+=v[ans[0]].size();

         //   printf("? %d\n",res);
            for(int i = 1;i<=200&&i<=ans[0];i++){
                int x=ans[0]-i;
                if(v[x].size()){
                    int l=ans[1]-v[x].back(),r=ans[1]-v[x][0];
                    int need=i*i;
                    if(r>=need)res+=r-max(need,l)+1;
                  //  printf("? %d %d %d\n",l,r,res);
                }
            }
           // printf("%d\n",res);
            v[ans[0]].push_back(ans[1]);
        }
        return res;
    }
};
