class Solution {
    public int numberOfSubstrings(String s) {
        int n=s.length();
        HashMap<Integer,Integer> h1=new HashMap<>();
        HashMap<Integer,Integer> h2=new HashMap<>();
        // h1.put(0,new ArrayList<Integer>());
        // h1.get(0).add(0);
        h1.put(0,0);
        h2.put(0,0);
        int cnt=0,ans=0;
        for(int i=1;i<=n;i++){
            if(s.charAt(i-1)=='0') cnt++;
            // System.out.println("I = "+i);
            for(int j=0;j<=200;j++){
                if(cnt-j<0) break;
                if(!h1.containsKey(cnt-j)) continue;
                int hi=i-h1.get(cnt-j);
                int lo=i-h2.get(cnt-j);
                int min=(j*j+j);
                // System.out.println("Lo = "+lo+" Hi = "+hi+" Min = "+min);
                if(min<=hi&& min>=lo) ans+=hi-min+1;
                else if(min<lo) ans+=hi-lo+1; 
            }
            // System.out.println("Ans = "+ans);
            if(!h1.containsKey(cnt)) h1.put(cnt,i);
            h2.put(cnt,i);
        }
        return ans;
    }
}
