class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> z, o;
        int n = s.size();
        for(int i=0; i<n; i++){
            if(s[i] == '0') z.push_back(i);
            else o.push_back(i);
        }

        z.push_back(n);
        int zc = 0, oc = 0;
        long long ans = 0;

        for(int i=0; i<n; i++){
//             For c = 0
            ans += *lower_bound(z.begin(), z.end(), i) - i;

            for(int c=1; c * c <= n; c++){
                int p0 = zc + c;
                int p1 = oc + c * c;
                if(p0 == z.size())   break;
                if(p1 > o.size())   break;
                int pz = z[p0 - 1];
                int po = o[p1 - 1];
                int nextZero = (p0 >= z.size() ? n : z[p0]);
                int lb = max(po, pz);
                // int nextZero = z[p0];
                // if(nextZero < po)   continue;
                // cout << i << " " << c << " " << nextZero << " " << lb << " " << nextZero - po << "\n";
                ans += max(0, nextZero - lb);
                // if(nextZero == n)   break;
            }   

            if(s[i] == '0') zc++;
            else oc++;

            // cout << ans << "\n";
        }


        return ans;
    }
};
