class Solution {
public:
    int n, zero[40005], m;
    int numberOfSubstrings(string s) {
        n = s.size();
        zero[0] = -1;
        m = 0;
        int ans = 0;
        for(int i = 0; i < n; ++i){
            if(s[i] == '0') zero[++m] = i;
            ans += i - zero[m];
            for(int j = m; j >= 1 && j >= m - 200; --j){
                int p = m - j + 1;
                int x = std::min(i + 1 - p - p * p, zero[j]);
                if(x > zero[j - 1]) ans += x - zero[j - 1];
            }
        }
        return ans;
    }
};
